// Parse HTML and extract all available court data
const cheerio = require('cheerio');
const moment = require('moment-timezone');

// Set timezone for Romania
moment.tz.setDefault('Europe/Bucharest');

const htmlContent = $input.first().json.data;
const $ = cheerio.load(htmlContent);

// Initialize results array
const availableCourts = [];
const currentTime = moment();

// Parse court availability (adapt selectors based on actual website structure)
try {
  // Look for elements containing court information
  $('.court-slot, .booking-slot, .time-slot').each(function() {
    const courtElement = $(this);

    // Extract court information
    const courtName = courtElement.find('.court-name, .court-title').text().trim() || 
                     courtElement.attr('data-court') || 'Court';

    const timeSlot = courtElement.find('.time, .slot-time').text().trim() || 
                    courtElement.attr('data-time');

    const status = courtElement.find('.status, .availability').text().trim().toLowerCase() ||
                  courtElement.attr('data-status') || '';

    const date = courtElement.find('.date').text().trim() || 
                courtElement.attr('data-date') || moment().format('YYYY-MM-DD');

    // Check if court is available ("Liber" means available in Romanian)
    if (status.includes('liber') || status.includes('available') || 
        courtElement.hasClass('available') || courtElement.hasClass('liber')) {

      // Parse time slot
      const timeMatch = timeSlot.match(/(\d{1,2})[:\.]?(\d{2})\s*-\s*(\d{1,2})[:\.]?(\d{2})/);
      if (timeMatch) {
        const startHour = parseInt(timeMatch[1]);
        const startMinute = parseInt(timeMatch[2] || '0');
        const endHour = parseInt(timeMatch[3]);
        const endMinute = parseInt(timeMatch[4] || '0');

        // Create moment objects for start and end times
        const slotStart = moment(date).hour(startHour).minute(startMinute);
        const slotEnd = moment(date).hour(endHour).minute(endMinute);

        // Only include future time slots
        if (slotStart.isAfter(currentTime)) {
          const dayOfWeek = slotStart.day(); // 0 = Sunday, 1 = Monday, etc.
          const isWeekend = dayOfWeek === 0 || dayOfWeek === 6;

          availableCourts.push({
            id: `${courtName.replace(/\s+/g, '')}-${slotStart.format('YYYY-MM-DD-HH-mm')}`,
            sport: 'tennis',
            venue: 'Teren Sud Arena',
            courtName: courtName,
            date: slotStart.format('YYYY-MM-DD'),
            timeSlot: `${slotStart.format('HH:mm')}-${slotEnd.format('HH:mm')}`,
            startTime: slotStart.toISOString(),
            endTime: slotEnd.toISOString(),
            status: 'available',
            bookingUrl: courtElement.find('a').attr('href') || '',
            source: 'sudarena',
            lastChecked: currentTime.toISOString(),
            dayOfWeek: slotStart.format('dddd'),
            isWeekend: isWeekend,
            hour: slotStart.hour()
          });
        }
      }
    }
  });

  // If no specific court elements found, try alternative parsing methods
  if (availableCourts.length === 0) {
    // Look for table structures
    $('table tr, .schedule-row, .booking-row').each(function() {
      const row = $(this);
      const cells = row.find('td, .cell, .slot');

      if (cells.length > 0) {
        cells.each(function(index) {
          const cell = $(this);
          const cellText = cell.text().trim().toLowerCase();

          if (cellText.includes('liber') || cellText.includes('available')) {
            // Extract time from header or data attributes
            const timeHeader = cell.attr('data-time') || 
                             row.find('th').eq(index).text().trim() ||
                             $('thead th').eq(index).text().trim();

            if (timeHeader) {
              const timeMatch = timeHeader.match(/(\d{1,2})[:\.]?(\d{2})/);
              if (timeMatch) {
                const hour = parseInt(timeMatch[1]);
                const minute = parseInt(timeMatch[2] || '0');

                const slotStart = moment().hour(hour).minute(minute);
                const slotEnd = moment().hour(hour + 1).minute(minute);

                if (slotStart.isAfter(currentTime)) {
                  const dayOfWeek = slotStart.day();
                  const isWeekend = dayOfWeek === 0 || dayOfWeek === 6;

                  availableCourts.push({
                    id: `court-${index}-${slotStart.format('YYYY-MM-DD-HH-mm')}`,
                    sport: 'tennis',
                    venue: 'Teren Sud Arena',
                    courtName: `Court ${index + 1}`,
                    date: slotStart.format('YYYY-MM-DD'),
                    timeSlot: `${slotStart.format('HH:mm')}-${slotEnd.format('HH:mm')}`,
                    startTime: slotStart.toISOString(),
                    endTime: slotEnd.toISOString(),
                    status: 'available',
                    bookingUrl: '',
                    source: 'sudarena',
                    lastChecked: currentTime.toISOString(),
                    dayOfWeek: slotStart.format('dddd'),
                    isWeekend: isWeekend,
                    hour: slotStart.hour()
                  });
                }
              }
            }
          }
        });
      }
    });
  }

  // Sort results by date and time
  availableCourts.sort((a, b) => new Date(a.startTime) - new Date(b.startTime));

  return {
    courts: availableCourts,
    totalFound: availableCourts.length,
    lastUpdated: currentTime.toISOString(),
    source: 'sudarena',
    nextCheck: moment().add(30, 'minutes').toISOString()
  };

} catch (error) {
  console.error('Parsing error:', error);
  return {
    courts: [],
    totalFound: 0,
    lastUpdated: currentTime.toISOString(),
    source: 'sudarena',
    error: error.message,
    nextCheck: moment().add(30, 'minutes').toISOString()
  };
}

// Format court data for Discord message with time filtering
const moment = require('moment-timezone');
moment.tz.setDefault('Europe/Bucharest');

const data = $input.first().json;
const allCourts = data.courts || [];

// Apply time filtering: weekdays after 6 PM, weekends anytime
const filteredCourts = allCourts.filter(court => {
  // Weekend courts (Saturday=6, Sunday=0) - show any time
  if (court.isWeekend) {
    return true;
  }
  // Weekday courts - only show after 6 PM (18:00)
  return court.hour >= 18;
});

if (filteredCourts.length === 0) {
  return {
    hasAvailability: false,
    message: "üòî **No Available Courts**\n\nüìÖ **No courts matching your time preferences**\nüìù Showing: Weekends (anytime) ‚Ä¢ Weekdays (after 6 PM)\n‚è∞ Checked: " + moment().format('HH:mm DD/MM/YYYY') + "\n\nI'll keep monitoring and notify you when courts become available!",
    lastUpdated: data.lastUpdated,
    nextCheck: data.nextCheck
  };
}

// Group filtered courts by date
const courtsByDate = {};
filteredCourts.forEach(court => {
  if (!courtsByDate[court.date]) {
    courtsByDate[court.date] = [];
  }
  courtsByDate[court.date].push(court);
});

// Build Discord message
let message = "üéæ **Tennis Courts Available**\n\n";
message += "üìù *Showing: Weekends (anytime) ‚Ä¢ Weekdays (after 6 PM)*\n\n";

Object.keys(courtsByDate).sort().forEach(date => {
  const dateFormatted = moment(date).format('dddd, DD/MM/YYYY');
  const isToday = moment(date).isSame(moment(), 'day');
  const isTomorrow = moment(date).isSame(moment().add(1, 'day'), 'day');

  let dayLabel = dateFormatted;
  if (isToday) dayLabel = `Today (${moment(date).format('DD/MM/YYYY')})`;
  else if (isTomorrow) dayLabel = `Tomorrow (${moment(date).format('DD/MM/YYYY')})`;

  message += `üìÖ **${dayLabel}**\n`;

  courtsByDate[date].forEach(court => {
    message += `‚Ä¢ ${court.courtName} - ${court.timeSlot} ‚ö° Available\n`;
  });

  message += "\n";
});

message += `‚è∞ Last updated: ${moment(data.lastUpdated).format('HH:mm DD/MM/YYYY')}\n`;
message += `üîÑ Next check: ${moment(data.nextCheck).format('HH:mm')}\n\n`;
message += "React with üìÖ to get booking info";

return {
  hasAvailability: true,
  message: message,
  courts: filteredCourts,
  totalCourts: filteredCourts.length,
  lastUpdated: data.lastUpdated,
  nextCheck: data.nextCheck
};

// Parse Discord command and extract parameters
const webhookData = $input.first().json;

// Extract command from Discord webhook data
let command = '';
let parameters = {};

// Handle different Discord webhook formats
if (webhookData.data && webhookData.data.name) {
  // Slash command format
  command = webhookData.data.name;
  if (webhookData.data.options) {
    webhookData.data.options.forEach(option => {
      parameters[option.name] = option.value;
    });
  }
} else if (webhookData.content) {
  // Message content format (legacy commands)
  const content = webhookData.content.toLowerCase().trim();
  if (content.startsWith('!tennis')) {
    const parts = content.split(' ');
    if (parts.length > 1) {
      command = parts[1]; // 'today', 'week', etc.
      if (parts.length > 2) {
        parameters.date = parts[2];
      }
    }
  }
}

// Set default command if none provided
if (!command) {
  command = 'today';
}

return {
  command: command,
  parameters: parameters,
  userId: webhookData.member?.user?.id || webhookData.user?.id || 'unknown',
  userName: webhookData.member?.user?.username || webhookData.user?.username || 'Unknown User',
  channelId: webhookData.channel_id,
  guildId: webhookData.guild_id,
  originalData: webhookData
};

// Parse HTML for on-demand requests with date filtering
const cheerio = require('cheerio');
const moment = require('moment-timezone');

moment.tz.setDefault('Europe/Bucharest');

const htmlContent = $input.first().json.data;
const command = $input.all()[1].json.command;
const parameters = $input.all()[1].json.parameters;

const $ = cheerio.load(htmlContent);
const availableCourts = [];
const currentTime = moment();

// Determine date range based on command
let startDate, endDate;

switch (command) {
  case 'today':
    startDate = moment().startOf('day');
    endDate = moment().endOf('day');
    break;
  case 'week':
    startDate = moment().startOf('day');
    endDate = moment().add(7, 'days').endOf('day');
    break;
  default:
    if (parameters.date) {
      startDate = moment(parameters.date).startOf('day');
      endDate = moment(parameters.date).endOf('day');
    } else {
      startDate = moment().startOf('day');
      endDate = moment().endOf('day');
    }
}

// Parse court availability (same logic as main parser)
try {
  $('.court-slot, .booking-slot, .time-slot').each(function() {
    const courtElement = $(this);

    const courtName = courtElement.find('.court-name, .court-title').text().trim() || 
                     courtElement.attr('data-court') || 'Court';

